use employees;    /*    Affichez le nom du département,    le nom du manager, le prénon du manager,    sa date de début et sa date de fin de fonction. (On utilisera la table dept_manager qui est la table intermédiaire entre employees et departments).     */select departments.dept_name , employees.first_name ,employees.last_name ,dept_manager.from_date  ,dept_manager.to_datefrom employeesinner join dept_manager on employees.emp_no=dept_manager.emp_noinner join departments on dept_manager.dept_no=departments.dept_no;/*Affichez le nom, prénom des employés qui ont le titre de manager; */ select employees.first_name ,employees.last_name, titles.title from employees inner join titles on employees.emp_no=titles.emp_no where titles.title like 'manager';/* Afficher le nom, le prénom et le département de chaque employé. */select employees.first_name ,employees.last_name,departments.dept_namefrom employeesinner join dept_manager on employees.emp_no=dept_manager.emp_noinner join  departments on dept_manager.dept_no=departments.dept_noorder by departments.dept_name asc;/*Affichez les employés qui ont travaillé dans le département 'Finance'.*/select employees.first_name ,employees.last_name,departments.dept_namefrom employees         inner join dept_emp on employees.emp_no=dept_emp.emp_no         inner join  departments on dept_emp.dept_no=departments.dept_nowhere departments.dept_name like 'finance';/* Affichez les employés qui ont travaillé dans le département 'Finance' et qui ont été manager de ce département. */select employees.first_name ,employees.last_name,departments.dept_name,titles.titlefrom employees inner join titles on employees.emp_no=titles.emp_noinner join dept_manager on employees.emp_no=dept_manager.emp_noinner join  departments on dept_manager.dept_no=departments.dept_nowhere titles.title like 'manager' and departments.dept_name like 'finance';